import 'package:flutter/material.dart';

class Biblioteca extends StatelessWidget {
  const Biblioteca({Key? key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Biblioteca', style: TextStyle(color: Color.fromARGB(255, 255, 255, 255), fontSize: 25)),
        backgroundColor: const Color.fromARGB(255, 121, 2, 101 ),
      ),
      body: ListView(
        children: const [
          CardItem('Terraria', '¡Cava, lucha, explora, construye! Con este juego de aventuras repleto de acción nada es imposible.', 'data:image/jpeg;base64,'),
          CardItem('Stardew Valley', 'Acabas de heredar la vieja parcela agrícola de tu abuelo de Stardew Valley.', 'data:image/jpeg;base64,'),
        ],
      ),
    );
  }
}

class CardItem extends StatelessWidget {
  final String title;
  final String subtitle;
  final String imageUrl;
  const CardItem(this.title, this.subtitle, this.imageUrl, {super.key});

  @override
  Widget build(BuildContext context) {
    return Card(
      child: ListTile(
        title: Text(title),
        subtitle: Text(subtitle),
         leading: Image.network(
          imageUrl,
          width: 60,
          height: 60,
          fit: BoxFit.cover,
        ),
        
      ),
    );
  }
}
