import 'package:flutter/material.dart';

class Noticias extends StatelessWidget {
  const Noticias({Key? key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Noticias', style: TextStyle(color: Color.fromARGB(255, 255, 255, 255), fontSize: 25)),
        backgroundColor: const Color.fromARGB(255, 121, 2, 101),
      ),
      body: ListView(
        children: const [
          CardItem('Proximamente: ARK 2', 'Lanzamiento: 2024', 'data:image/jpeg;base64,'),
          CardItem('Proximamente: GTA VI', 'Lanzamiento: 2038', 'data:image/png;base64,'),
          CardItem('Proximamente: Ajedrez 2', 'Lanzamiento: 2065', 'data:image/jpeg;base64,'),

        ],
      ),
    );
  }
}

class CardItem extends StatelessWidget {
  final String title;
  final String subtitle;
  final String imageUrl;

  const CardItem(this.title, this.subtitle, this.imageUrl, {super.key});

  @override
  Widget build(BuildContext context) {
    return Card(
      child: ListTile(
        title: Text(title),
        subtitle: Text(subtitle),
                leading: Image.network(
          imageUrl,
          width: 200,
          height: 200,
        ),
        
      ),
    );
  }
}
