import 'package:flutter/material.dart';

class Productos extends StatelessWidget {
  const Productos({Key? key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Productos', style: TextStyle(color: Color.fromARGB(255, 255, 255, 255), fontSize: 25)),
        backgroundColor: const Color.fromARGB(255, 121, 2, 101),
      ),
      body: ListView(
        children: const [
          CardItem('ARK: Survival Evolved', '37.500','data:image/jpeg;base64,' ),
          CardItem('ARK: Survival Ascended', '190.000', 'data:image/jpeg;base64,'),
          CardItem('PixARK', '54.500', 'data:image/jpeg;base64,'),

        ],
      ),
    );
  }
}

class CardItem extends StatelessWidget {
  final String title;
  final String subtitle;
  final String imageUrl;

  const CardItem(this.title, this.subtitle, this.imageUrl, {super.key});

  @override
  Widget build(BuildContext context) {
    return Card(
      child: ListTile(
        title: Text(title),
        subtitle: Text(subtitle),
        leading: Image.network(
          imageUrl,
          width: 60,
          height: 60,
          fit: BoxFit.cover,
        ),
        
      ),
    );
  }
}
