class CardItem extends StatelessWidget {
  final String title;
  final String subtitle;
  final String imageUrl;

  const CardItem(this.title, this.subtitle, this.imageUrl, {super.key});

  @override
  Widget build(BuildContext context) {
    return Card(
      child: ListTile(
        title: Text(title),
        subtitle: Text(subtitle),
                leading: Image.network(
          imageUrl,
          width: 200,
          height: 200,
        ),
        
      ),
    );
  }
}
